n0:createOracleTrigger.n0:source:
  CREATE OR REPLACE EDITIONABLE TRIGGER "WKSP_ADMCI"."BIU_EBA_DEMO_MD_COMMENTS" 
    before insert or update on eba_demo_md_comments
    for each row
begin
    if :new.id is null then
        :new.id := to_number(sys_guid(), 'XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX');
    end if;
    if inserting then
        :new.created    := current_timestamp;
        :new.created_by := nvl(wwv_flow.g_user,user);
    end if;
    :new.updated    := current_timestamp;
    :new.updated_by := nvl(wwv_flow.g_user,user);
end;

ALTER TRIGGER "WKSP_ADMCI"."BIU_EBA_DEMO_MD_COMMENTS" ENABLE
